import React, { Component } from 'react';
import './App.css';

class App extends Component {

  state = {

    username: '',
    email: '',
    password: '',
    checked: false,
  }

  handleChange = (e) => {
    console.log(e.target.type)
    console.log(e.target.name)
    const name = e.target.name;
    const value = e.target.value;

    this.setState({
      [name]: value
    })
  }
  render() {



    return (
      <div className="App">
        <form>
          <label htmlFor="user">Your Name :
            <input type="text" id="user" name="username" value={this.state.username} onChange={this.handleChange} />
          </label>
        </form>


        <form>
          <label htmlFor="email">Your email adress :
            <input type="email" id="email" name="email" value={this.state.email} onChange={this.handleChange} />
          </label>
        </form>


        <form>
          <label htmlFor="password">Your password :
            <input type="password" id="password" name="password" value={this.state.password} onChange={this.handleChange} />
          </label>
        </form>

        <form>
          <label htmlFor="checkbox">
            <input type="checkbox" id="checkbox" name="checkbox" value={this.state.checked} onChange={this.handleChange} />
            Confirm that you accept terms and conditions</label>
        </form>

      </div>
    );
  }
}

export default App;
