import React, { Component } from 'react';
import './App.css';

class App extends Component {

  state = {

    username: '',
    email: '',
    password: '',
    checked: false,

    errors: {
      username: true,
      email: true,
      password: true,
      checked: true,
    },
  }

  messages = {
    username_incorrect: "name has to contain 8 characters",
    email_incorrect: "email has to contain @",
    password_incorrect: "password has to be longer than 6 characters ",
    checked_incorrect: "checked is not confirmed ",
  }

  handleChange = (e) => {

    const type = e.target.type;
    const name = e.target.name;
    const value = e.target.value;

    if (type === "text" || type === "email" || type === "password") {
      this.setState({
        [name]: value
      })

    } else if (type === "checkbox") {
      const checked = e.target.checked
      this.setState({
        [name]: checked,
      })
    }
  }

  handleSumbit = (e) => {
    e.prevent.Defoult();
    console.log('it is wors')
  }

  render() {

    const { username, email, password, checked } = this.state.errors;


    return (
      <div className="App">
        <form onSubmit={this.handleSumbit}>
          <label htmlFor="user">Your Name :
            <input type="text" id="user" name="username" value={this.state.username} onChange={this.handleChange} />
            {username && <span>{this.messages.username_incorrect}</span>}
          </label>




          <label htmlFor="email">Your email adress :
            <input noValidate type="email" id="email" name="email" value={this.state.email} onChange={this.handleChange} />

          </label>



          <label htmlFor="password">Your password :
            <input type="password" id="password" name="password" value={this.state.password} onChange={this.handleChange} />
          </label>

          <label htmlFor="checkbox">
            <input type="checkbox" id="checkbox" name="checked" value={this.state.checked} onChange={this.handleChange} />
            Confirm that you accept terms and conditions</label>
        </form>

        <button>Send</button>

      </div>
    );
  }
}

export default App;
